// src/Components/Navbar.jsx
import React, { useContext } from 'react';
import { ThemeContext } from '../context/ThemeContext'; // Asegúrate de importar el contexto

const Navbar = () => {
  const { theme, toggleTheme } = useContext(ThemeContext); // Usa el contexto

  return (
    <nav className={theme}> {/* Apl}
      <button onClick={toggleTheme}>Change theme</button>
      {/* Agrega los links correspondientes */}
      <ul>
        <li><a href="/">Home</a></li>
        <li><a href="/contacto">Contacto</a></li>
        <li><a href="/favs">Destacados</a></li>
      </ul>
    </nav>
  );
};

export default Navbar;
