import React, { createContext, useReducer } from 'react';

const FavoritesContext = createContext();

const favoritesReducer = (state, action) => {
  switch (action.type) {
    case 'LOAD_FAVORITES':
      return { ...state, favorites: action.payload };
    case 'REMOVE_FAVORITE':
      return { ...state, favorites: state.favorites.filter(fav => fav.id !== action.payload) };
    default:
      return state;
  }
};

const FavoritesProvider = ({ children }) => {
  const [state, dispatch] = useReducer(favoritesReducer, { favorites: [] });

  React.useEffect(() => {
    const savedFavorites = JSON.parse(localStorage.getItem("favs")) || [];
    dispatch({ type: 'LOAD_FAVORITES', payload: savedFavorites });
  }, []);

  return (
    <FavoritesContext.Provider value={{ state, dispatch }}>
      {children}
    </FavoritesContext.Provider>
  );
};

export { FavoritesProvider };
export default FavoritesContext;
