import React, { useContext } from 'react';
import { Link } from 'react-router-dom'; // Importamos Link de react-router-dom
import { ThemeContext } from '../context/ThemeContext'; // Importamos el contexto de Theme

//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context
const Navbar = () => {
  const { theme, toggleTheme } = useContext(ThemeContext); // Consumimos el theme y la función para cambiarlo

  return (
    <nav className={theme === 'dark' ? 'navbar-dark' : 'navbar-light'}> {/* Aplicamos la clase según el theme */}
      <ul>
        <li><Link to="/">Home</Link></li>
        <li><Link to="/contact">Contacto</Link></li>
        <li><Link to="/favs">Destacados</Link></li>
      </ul>
      <button onClick={toggleTheme}>Change theme</button> {/* Botón para cambiar de tema */}
    </nav>
  );
}

export default Navbar;
