import React from "react";
import { Link } from 'react-router-dom';

const Card = ({ name, username, id }) => {

  const addFav = () => {
    // Lógica para agregar la card a favoritos en el localStorage
    const favs = JSON.parse(localStorage.getItem("favs")) || [];
    const newFav = { id, name, username };
    localStorage.setItem("favs", JSON.stringify([...favs, newFav]));
    alert(`${name} añadido a favoritos`);
  };

  return (
    <div className="card">
      {/* El nombre del doctor */}
      <h3>{name}</h3>
      {/* Username del doctor */}
      <p>{username}</p>
      {/* Enlace hacia la página de detalle del doctor */}
      <Link to={`/users/${id}`}>
        <img src={`https://randomuser.me/api/portraits/men/${id}.jpg`} alt={`${name}`} />
      </Link>
      {/* Botón para agregar a favoritos */}
      <button onClick={addFav} className="favButton">Add to favs</button>
    </div>
  );
};

export default Card;
