import React from "react";
import { Link } from 'react-router-dom';

const Card = ({ name, username, id }) => {

  const addFav = () => {
    const favs = JSON.parse(localStorage.getItem("favs")) || [];
    const existingFav = favs.find(fav => fav.id === id);

    if (!existingFav) {
      const newFav = { id, name, username };
      localStorage.setItem("favs", JSON.stringify([...favs, newFav]));
      alert(`${name} a침adido a favoritos`);
    } else {
      alert(`${name} ya est치 en favoritos`);
    }
  };

  return (
    <div className="card">
      <h3>{name}</h3>
      <p>{username}</p>
      <Link to={`/users/${id}`}>
        <img src={`https://randomuser.me/api/portraits/men/${id}.jpg`} alt={`${name}`} />
      </Link>
      <button onClick={addFav} className="favButton">Add to favs</button>
    </div>
  );
};

export default Card;
