// src/Components/Card.jsx
import React, { useContext } from "react";
import { Link } from 'react-router-dom';
import FavoritesContext from '../Context/FavoritesContext';

const Card = ({ name, username, id }) => {
  const { state, dispatch } = useContext(FavoritesContext);

  const addFav = () => {
    const isAlreadyFav = state.favorites.some(fav => fav.id === id);
    if (!isAlreadyFav) {
      const newFav = { id, name, username };
      dispatch({ type: 'ADD_FAV', payload: newFav });
      alert(`${name} a침adido a favoritos`);
    } else {
      alert(`${name} ya est치 en favoritos`);
    }
  };

  return (
    <div className="card">
      <h3>{name}</h3>
      <p>{username}</p>
      <Link to={`/users/${id}`}>
        <img src={`https://randomuser.me/api/portraits/men/${id}.jpg`} alt={`${name}`} />
      </Link>
      <button onClick={addFav} className="favButton">Add to favs</button>
    </div>
  );
};

export default Card;
