// src/Components/UserDetail.jsx
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function UserDetail() {
  const { id } = useParams(); // Extrae el ID de la URL
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Llama a la API usando el ID del usuario
    axios.get(`https://jsonplaceholder.typicode.com/users/${id}`)
      .then(response => {
        setUser(response.data); // Guarda los detalles del usuario en el estado
      })
      .catch(error => {
        console.error('Hubo un error al obtener los detalles del usuario:', error);
      });
  }, [id]);

  if (!user) return <p>Cargando...</p>;

  return (
    <div>
      <h1>{user.name}</h1>
      <p>Email: {user.email}</p>
      <p>Teléfono: {user.phone}</p>
      <p>Dirección: {user.address.street}, {user.address.city}</p>
      <p>Empresa: {user.company.name}</p>
    </div>
  );
}

export default UserDetail;
