// src/Components/UserDetail.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom'; // Para obtener el ID de la URL

function UserDetail() {
    const { id } = useParams(); // Obtener el ID del usuario de la URL
    const [user, setUser] = useState(null);

    useEffect(() => {
        // Llamada a la API para obtener los detalles del usuario
        axios.get(`https://jsonplaceholder.typicode.com/users/${id}`)
            .then(response => {
                setUser(response.data);
            })
            .catch(error => {
                console.error('Hubo un error al obtener los detalles del usuario:', error);
            });
    }, [id]);

    return (
        <div>
            {user ? (
                <div>
                    <h1>{user.name}</h1>
                    <p>Email: {user.email}</p>
                    <p>Tel√©fono: {user.phone}</p>
                    <p>Website: {user.website}</p>
                </div>
            ) : (
                <p>Cargando detalles del usuario...</p>
            )}
        </div>
    );
}

export default UserDetail;
